template<typename T = int>
inline T read(void);

int N;

deque < int > deq;
stack < int > st;
bool poped(false);
ll cnt(0);

void dfs(int dep = 1){
    if(dep > N * 2)return (void)cnt++;
    if(!deq.empty()){
        st.push(deq.front());
        deq.pop_front();
        dfs(dep + 1);
        deq.push_front(st.top());
        st.pop();
    }
    if(!st.empty()){
        int tmp = st.top();
        if(poped){
            st.pop();
            dfs(dep + 1);
            st.push(tmp);
        }else if(tmp){
            poped = true;
            st.pop();
            dfs(dep + 1);
            st.push(tmp);
            poped = false;
        }
    }

}

